Script.Inputs = {        
    "Container":"reference",  // Example: 500
    "Rive File": "string",  // Example: 500
    "State Machine": "string",  // Example: 500
    "Artboard": "string",  // Example: 500
    "Width": "number",  // Example: 500
    "Width": "number",  // Example: 500
    "Height": "number",  // Example: 200 (Fixed typo: 'Heght' → 'Height')
    "Position X": "number",  // Example: 400
    "Position Y": "number"  // Example: 100
};

Script.Signals.Run = function () {
    // Load Rive.js
    let script = document.createElement("script");
    script.src = "https://unpkg.com/@rive-app/canvas";
    script.onload = function () {
        let canvas = document.createElement("canvas");
        canvas.id = "canvas";

        // Use Inputs for width, height, and position
        canvas.width = Script.Inputs["Width"] * 0.66;
        canvas.height = Script.Inputs["Height"] * 0.66;
        
        // Apply dynamic positioning
        canvas.style.position = "absolute";  
        canvas.style.top = Script.Inputs["Position Y"] + "px";  
       // canvas.style.left = Script.Inputs["Position X"] + "px";  

        canvas.style.left = "50%";  
        //canvas.style.top = "50%";  
        canvas.style.transform = "translate(-50%, 0)"; 

        document.body.appendChild(canvas);

        let r1 = new rive.Rive({
            src: "riv/" + Script.Inputs["Rive File"] + ".riv",
            //src: Script.Inputs["Rive File"] + ".riv",
            canvas: canvas,
            autoplay: true,
            artboard: Script.Inputs["Artboard"],
            stateMachines: Script.Inputs["State Machine"],
            onLoad: () => {
                r1.resizeDrawingSurfaceToCanvas();
            },
        });
    };
    document.head.appendChild(script);
};

