const txtSelector = '.animateText';
const scrollerSelector = '.scroll-wrap-home';

document.querySelectorAll(txtSelector).forEach(el => {
  el.style.display = 'block';

  const split = new SplitType(el, { types: 'words, chars' });

  gsap.fromTo(split.chars,
    { scale: 1, y: 10, opacity: 0.36 },
    {
      scale: 1, y: 0, opacity: 1,
      ease: 'power1.inOut',
      stagger: { each: 0.2, from: 'start' },
      scrollTrigger: {
        trigger: el,
        start: 'top 80%',
        end: 'bottom 50%',
        scrub: true,
        markers: false,
        scroller: scrollerSelector
      }
    }
  );
});

const lenis = new Lenis({
  wrapper: document.querySelector('.scroll-wrap-home'),
  content: document.querySelector('.scroll-content-home'), // ✅ just this one line
  duration: 0.75,
  easing: (t) => t * (2 - t),
  lerp: 0.04,
  smooth: true,
  smoothTouch: true
});

function raf(time) {
  lenis.raf(time);
  requestAnimationFrame(raf);
}


requestAnimationFrame(raf);

