.video-container {
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  overflow: hidden;
}

.video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  display: block;
  
}

.scroll-wrapper {
    width: 100%;
    overflow: hidden;
    position: relative;
  }

  .scroll-track {
    display: flex;
    width: max-content;
    animation: scroll-left 40s linear infinite;
  }

@keyframes scroll-left {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
}

